			if 'brad' in name:
				posture = 'HandsAtSide_Motex'
			if 'elder' in name or 'doctor' in name:
				posture = 'LHandOnHip_Motex'
			# Add gesture mapping
			gMap.addGestureMapping('HandsAtSide_RArm_GestureYou', 'YOU', '', 'RIGHT_HAND', '', posture)
