def playGesture(chrName, lexeme, hand, type='', start=0, ready=0, stroke=0, stroke_end=0):
	''' Chararacter name, lexeme, hand to use, type of gesture, start point, ready point, stroke, stroke end
		Example: playGesture(brad, 'EMBLEM', 'LEFT_HAND', 'GREETING', start=0, ready=1, stroke=0, stroke_end=2.5) '''
	# No pausing
	if stroke_end == 0:
		bml.execBML(chrName, '<gesture lexeme="' + lexeme + '" type="' + type + \
					'" mode="' + hand + '" start="' + str(start) + '" ready="' + str(ready) + '"/>')
	# Pauses in between and perlin noise
	if stroke_end != 0:
		scale = 1.0
		bml.execBML(chrName, '<gesture lexeme="' + lexeme + '" type="' + type + \
					'" mode="' + hand + '" stroke="' + str(stroke) + '" stroke_end="' + str(stroke_end) + \
					'" sbm:joint-range="l_shoulder r_shoulder spine1 spine4" sbm:frequency="' + str(0.03 * scale) + '" sbm:scale="' + str(0.02 * scale) +'"/>')
	#bml.execBML('brad', '<gesture id="a" lexeme="YOU" mode="RIGHT_HAND" stroke="0" stroke_end="2" sbm:joint-range="l_shoulder" sbm:frequency="0.03" sbm:scale="0.02"/>')
	#bml.execBML('brad', '<gesture id="b" lexeme="RIGHT" mode="RIGHT_HAND" stroke="3" stroke_end="5" sbm:joint-range="l_shoulder" sbm:frequency="0.03" sbm:scale="0.02"/>')
	#bml.execBML('brad', '<gesture id="c" lexeme="OFFER" mode="RIGHT_HAND" stroke="b:stroke_end" stroke_end="7" sbm:joint-range="r_shoulder" sbm:frequency="0.03" sbm:scale="0.02"/>')
	#bml.execBML('*', '<gesture lexeme="" type="YOU" mode="LEFT_HAND"/>')
	#bml.execBML('brad1', '<gesture mode="LEFT_HAND" sbm:frequency="0.03" sbm:joint-range="l_shoulder" sbm:scale="0.02" stroke="2" stroke_end="4" type="RHETORICAL"/>')